  const [error, setError] = useState(null);

  const execute = async (optimisticData) => {
    const previousData = data;
    setData(optimisticData);

    try {
      const result = await asyncFn();
      setData(result);
    } catch (err) {
      setData(previousData);
      setError(err);
    }
  };

  return { data, error, execute };
};

